export function getCartItems(){return JSON.parse(localStorage.getItem("cart"))||[]}export function getCartItemsQty(t){let e=0;return t.forEach((t=>{e+=t.quantity})),e}export function getCartLength(){return getCartItems().length}export function getItemDictionary(t){let e={};return t.forEach((t=>{const n=parseFloat(t.price);let a=void 0!==t.quantity?t.quantity:1;e[t.name]?e[t.name].quantity=e[t.name].quantity+a:e[t.name]={id:t.id,name:t.name,cartName:t.cartName,price:n,quantity:a,subtotal:0},e[t.name].subtotal+=n})),e}export function applyAnimationClass(t,e){t.classList.add(e),t.addEventListener("animationend",(function(){t.classList.remove(e)}),{once:!0})}export function disableCheckoutBtn(){const t=document.querySelector("#go-to-checkout-btn");t.classList.add("disabled"),t.href="#"}export function enableCheckoutBtn(){const t=document.querySelector("#go-to-checkout-btn");t.classList.remove("disabled"),t.href="/checkout"}function initCart(){const t=getCartItems();getCartItemsQty(t),getItemDictionary(t)}