export function homeInit(e){let t=e.querySelector("#home-zx9-speaker-wrapper"),s=e.querySelector("#speaker-bg-circles"),r=e.querySelector("#zx9-image");t.addEventListener("mouseover",function(){s.classList.add("active"),r.classList.add("active")}),t.addEventListener("mouseout",function(){s.classList.remove("active"),r.classList.remove("active")})}